import styled from "styled-components";

export const LoginBox = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    height: 100%;
    width: 100%;
    Position: relative;
`;

export const Logo = styled.img`
    height: 60px;
    margin-top: 13%;
    margin-bottom: 10px;
`;

export const InputWrap = styled.input`
    Position: relative;
`;

export const InputBox = styled.input`
    height: 40px;
    width: 400px;
    border-radius: 4px;
    margin-bottom: 5px;
    border: 1px solid #6C6C6C;
    padding-left: 15px;
`;

export const EyeIcon = styled.img`
    position : absolute;
`;

export const LoginError = styled.div`
    width: 419px;
    text-align: left;
    color: red;
    margin-top: 10px;
    margin-bottom: 10px;
    font-size: 13px;
    padding-left: 10px;
`;

export const LoginBtn = styled.button`
    margin-top: 5px;
    height: 45px;
    width: 419px;
    border-radius: 4px;
    text-align: center;
    color: white;
    background-color: black;
    font-weight: bold;
    &:hover{  
    background-color : #444444;
    }
`;

export const FindWrap = styled.div`
    display: flex;
    width: 419px;
    flex-direction: row-reverse;
    margin-top: 5px;
`;

export const FindBox = styled.div`
    color: #6C6C6C;
`;

export const Contour = styled.div`
    margin-right: 10px;
    margin-left: 10px;
    border-right: 2px solid #D9D9D9;
    margin-top: 5px;
    height: 15px;
`;

export const JoinBtn = styled.button`
    margin-top: 10px;
    height: 45px;
    width: 419px;
    border-radius: 4px;
    text-align: center;
    color: black;
    background-color: white;
    font-weight: bold;
    border: 0.5px bold;
    &:hover{  
    background-color : #444444;
    }

`;
