    width: 150px;
    text-align: center;
    height: 100%;
    background-color: white;
    border-radius: 1.5px;
    font-weight: bold;
    &:hover{  
    background-color : black;
    color : white
  }
`;

export const Mypage = styled.div`
    border-left: 2px solid #D9D9D9;
